// services/apiService.js
import axios from 'axios';

// Doğru backend URL'sini kullanın
const API_BASE_URL = 'http://localhost:8081/api';

// Axios instance oluşturun
const api = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json'
  }
});

// API fonksiyonları
export const addParking = (parkingData) => {
  console.log('Otopark eklenecek veriler:', parkingData);
  return axios.post(`${API_BASE_URL}/admin/parkings`, parkingData, {
    headers: {
      'Content-Type': 'application/json'
    }
  });
};

export const getParkings = () => {
  return axios.get(`${API_BASE_URL}/parkings`);
};

export const getParkingById = (id) => {
  return axios.get(`${API_BASE_URL}/parkings/${id}`);
};

export const createParking = (parkingData) => {
  return api.post('/parkings', parkingData);
};

export const updateParking = (id, parkingData) => {
  return api.put(`/admin/parkings/${id}`, parkingData);
};

export const deleteParking = (id) => {
  return api.delete(`/admin/parkings/${id}`);
};

// Layout API çağrıları
export const getParkingLayout = (parkingId) => {
  return api.get(`/${parkingId}/layout`);
};

export const updateParkingLayout = (parkingId, layoutData) => {
  return api.post(`/${parkingId}/layout`, layoutData);
};

export const clearParkingLayout = (parkingId) => {
  return api.put(`/${parkingId}/clear-layout`);
};
